import React from 'react';
import { Routes, Route } from 'react-router-dom';
// import logo from './logo.svg'; 
import './App.css';
import Header from './components/Header';
// import Card from './components/Card/index.js';
import Drawer from './components/Drawer';
import Home from './pages/Home';
// import Home1 from './pages/Home1';
import Favorites from './pages/Favorites';
import axios from 'axios';

const AppContext = React.createContext({})

console.log(AppContext)

function App() {
  const [items, setItems] = React.useState([]);
  const [cartItems, setCartItems] = React.useState([
  ]);
  const [favorites, setFavorites] = React.useState([]);
  const [searchValue, setSearchValue] = React.useState('');
  const [cartOpened, setCartOpened] = React.useState(false);

  React.useEffect(() => {

    axios.get('http://localhost:3001/iems').then(res => {
      setItems(res.data);
      console.log(res)
    });
    axios.get('http://localhost:3001/cart').then(res => {
      setCartItems(res.data);
    });
    axios.get('http://localhost:3001/favorites').then(res => {
      setFavorites(res.data);
    });
  },
    []);
  const onAddToCart = (obj) => {
    if (cartItems.find((item) => Number(item.id) === Number(obj.id))) {
      axios.delete(`http://localhost:3001/cart/${obj.id}`);
      setCartItems((prev) => prev.filter((item) => Number(item.id) !== Number(obj.id)));
      //    console.log(obj)
    } else {
      axios.post('http://localhost:3001/cart', obj);
      setCartItems((prev) => [...prev, obj]);
    }
    console.log(obj)
  };
  const onRemoveItem = (id) => {
    console.log(id);
    axios.delete(`http://localhost:3001/cart/${id}`);
    console.log(id)
    setCartItems((prev) => prev.filter((item) => item.id !== id));
    console.log(id)
  };
  // console.log(cartItems);
  const onAddToFavorite = (obj) => {
    console.log(obj)
    if (favorites.find(favObj => favObj.id === obj.id)) {
      axios.delete(`http://localhost:3001/favorites/${obj.id}`);
      setFavorites((prev) => prev.filter((item) => item.id !== obj.id));
      //    console.log(obj)
    } else {
      axios.post('http://localhost:3001/favorites', obj);
      setFavorites((prev) => [...prev, obj])
      //    console.log(obj)
    };
  };
  const onChangeSearchInput = (event) => {
    // console.log(event.targetValue);
    setSearchValue(event.target.value);
  };
  return (
    <div className="wrapper clear">
      {cartOpened && <Drawer items={cartItems} onClose={() => setCartOpened(false)} onRemove={onRemoveItem} />}
      <Header onClickCart={() => setCartOpened(true)} />
      <Routes>
        <Route path='/' exect element={
          <Home
            cartItems={cartItems}
            items={items}
            searchValue={searchValue}
            setSearchValue={setSearchValue}
            onChangeSearchInput={onChangeSearchInput}
            onAddToCart={onAddToCart}
            onAddToFavorite={onAddToFavorite}
          />
        }>
        </Route>
        <Route path='/favorites' exect element={
          <Favorites items={favorites} onAddToFavorite={onAddToFavorite}
          />}>
        </Route>
      </Routes>
    </div>
  );
}

export default App;
