        </div>

        <div className='d-flex flex-wrap'>
          {/* <Card />
          <Card /> */}


          {items
            .filter((item) => item.title.toLowerCase().includes(searchValue.toLowerCase()))
            .map((item, index) => (
              <Card
                key={item.title}
                title={item.title}
                price={item.price}
                imageUrl={item.imageUrl}



                // onFavorite={() => console.log('Нажали фав')}
                onPlus={(obj) =>
                  // console.log(obj)}
