import React from 'react';
import { Routes, Route } from 'react-router-dom';
import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import Drawer from './components/Drawer';
import Home from './pages/Home';
import Favorites from './pages/Favorites';
import axios from 'axios';

// const arr = [
//   { title: 'Мужские Кроссовки Nike Blazer Mid Suede', price: 12999, imageUrl: '/img//sneakers/card-1.webp' },
//   { title: 'Мужские Кроссовки Nike Air Max 270', price: 15600, imageUrl: '/img//sneakers/card-2.webp' },
//   { title: 'Мужские Кроссовки Nike Blazer Mid Suede', price: 8499, imageUrl: '/img//sneakers/card-3.webp' },
//   { title: 'Кроссовки Puma X Aka Boku Future Rider', price: 10700, imageUrl: '/img//sneakers/card-4.webp' },

// ];

function App() {
  const [items, setItems] = React.useState([]);
  const [cartItems, setCartItems] = React.useState([
    // { "title": "Мужские Кроссовки Nike Blazer Mid Suede", "price": 12999, "imageUrl": "/img/sneakers/card-1.webp" }, { "title": "Мужские Кроссовки Nike Air Max 270", "price": 15600, "imageUrl": "/img/sneakers/card-2.webp" }
  ]);
  const [favorites, setFavorites] = React.useState([]);
  const [searchValue, setSearchValue] = React.useState('');

  const [cartOpened, setCartOpened] = React.useState(false);

  React.useEffect(() => {
    // fetch('https://65c8c883a4fbc162e11230f8.mockapi.io/api/v1/ReactSneackers/items')
    //   .then((res) => {
    //     return res.json();
    //   })
    //   .then((json) => {
    //     setItems(json);
    //   });

    axios.get('https://65c8c883a4fbc162e11230f8.mockapi.io/api/v1/ReactSneackers/items').then(res => {
      setItems(res.data);
      console.log()


    });

    axios.get('https://65c8c883a4fbc162e11230f8.mockapi.io/api/v1/ReactSneackers/cart').then(res => {
      setCartItems(res.data);


    });

  },
    []);

  const onAddToCart = (obj) => {
    axios.post('https://65c8c883a4fbc162e11230f8.mockapi.io/api/v1/ReactSneackers/cart', obj);
    console.log(obj)

    setCartItems((prev) => [...prev, obj]);
    console.log(obj)


    // alert (123)

  };

  const onRemoveItem = (id) => {


    console.log(id);
    axios.delete(`https://65c8c883a4fbc162e11230f8.mockapi.io/api/v1/ReactSneackers/cart/${id}`);
    console.log(id)

    setCartItems((prev) => prev.filter((item) => item.id !== id));
    console.log(id)
  };

  // console.log(cartItems);
  const onAddToFavorite = (obj) => {
    axios.post('https://65fe7b5bb2a18489b3861d31.mockapi.io/favorites', obj);
    console.log(obj)

    setFavorites((prev) => [...prev, obj]);

  };

  const onChangeSearchInput = (event) => {
    // console.log(event.targetValue);
    setSearchValue(event.target.value);


  };

  return (
    <div className="wrapper clear">

      {cartOpened && <Drawer items={cartItems} onClose={() => setCartOpened(false)} onRemove={onRemoveItem} />}
      <Header onClickCart={() => setCartOpened(true)} />
      <Router>
      <Routes>
        <Route path='/' exect element={

          <Home
            items={items}
            searchValue={searchValue}
            setSearchValue={setSearchValue}
            onChangeSearchInput={onChangeSearchInput}
            onAddToCart={onAddToCart}
            onAddToFavorite={onAddToFavorite}
          />

        }>
        </Route>

      </Routes>
      <Routes>
        <Route path='/favorites' exect element={<Favorites />} >

          <Favorites />
        </Route>
      </Routes>
      </Router>



    </div>
  );
}

export default App;
