// import styles from './Card.module.scss'


function Drawer({ onClose, onRemove, items = [] }) {
  return (

    <div className="overlay">
      <div className='drawer' >
        <h2 className='d-flex justify-between mb-20'>Корзина  <img onClick={onClose} className='removeBtn cu-p' src="/img/remove.svg"></img></h2>

        {items.length > 0 ? (
        <div> 

        <div className='items'>
          {items.map((obj) => (
            <div className='cartItem d-flex align-center mb-20'>
              <div style={{ backgroundImage: `url(${obj.imageUrl})` }} className='cartItemImg'></div>
              <div className='mr-20 flex'>
                <p className='mb-5'>{obj.title}</p>
                <b>{obj.price}</b>
              </div>
              <img onClick={() => onRemove(obj.id)} className='removeBtn' src="/img/remove.svg" alt="Remove"></img>
            </div>
            // <div className='cartItem d-flex align-center mb-20'>
            //     <div style={{ backgroundImage: 'url(/img/sneakers/card-1.webp)' }} className='cartItemImg'></div>
            //       <div className='mr-20 flex'>
            //         <p className='mb-5'>Мужские Кроссовки Nike Blazer Mid Suede</p>
            //         <b>12 999 руб.</b>
            //   </div>
            //   <img className='removeBtn' src="/img/remove.svg"></img>
            // </div>
          ))}

        </div>
        <div className='cartTotalBlock'>

          <ul>
            <li>
              <span>Итого:</span>
              <div> </div>
              <b>21 498 руб.</b>
            </li>
            <li>
              <span>Налог 5%:</span>
              <div></div>
              <b>1074 руб.</b>
            </li>
          </ul>
          <button className='greenButton'>Оформить заказ <img src="/img/strelka.svg" alt="Arrow" /></button>
        </div>
        </div>
        ): (
        <div className="cartEmpty d-flex align-center justify-center flex-column flex">
          <img className="mb-20" width="120px" height="120px" src="/img/empty-cart.jpg"/>
          <h2>Корзина пустая</h2>
          <p className="opacity-6">Добавьте хотябы одну пару кроссовок, чтобы сделать заказ.</p>
          <button onClick={onClose} className="greenButton">
            <img src="/img/arrow.svg" alt="Arrow"/>
            Вернуться назад
          </button>  
        </div>

        )}


      </div>


    </div >

  );
}
export default Drawer;
